<template>
  <div ref = "home" class="home">
    <home-header></home-header>
    <div class="swipe-box">
      <mt-swipe :auto="3000">
        <mt-swipe-item v-for="img in swipeImg" :key="img.index">
          <img :src="img.url" alt="pic">
        </mt-swipe-item>
      </mt-swipe>
    </div>
    <div>
      <channels :channellist = "channellist"></channels>
    </div>
    <!-- 商品分类 -->
    <div>
      <goods-classify :categorylist = "categoryList"></goods-classify>
    </div>  
    <div style="height:400px">
       <visited-list></visited-list>
    </div>
  </div>     
</template>

<script>
import { Swipe, SwipeItem } from "mint-ui";
import homeHeader from "@/components/homeheader";
import channels from "@/modules/home/channels";
import visitedList from "@/modules/home/visitedlist";
import goodsClassify from "@/modules/home/goodsclassify";
import { mapState } from "vuex";
import { getchannel,getcategory } from "@/servies/home";
var swipeImg = [
  { url: "http://yc.yonyou.com/mobile/market/src/assets/1.png", name: "111" },
  { url: "http://yc.yonyou.com/mobile/market/src/assets/2.png", name: "222" },
  { url: "http://yc.yonyou.com/mobile/market/src/assets/3.png", name: "333" }
];
export default {
  components: {
    homeHeader,
    channels,
    goodsClassify,
    visitedList
  },
  mounted() {
    this.getchanelData();
    this.getcategyData();
  },
  computed: {
    ...mapState({
      channellist: state => state.home.channellist,
      categoryList: state => state.global.categoryList
    })
  },
  methods: {
    getcategyData () {
      getcategory(0).then(response => {
        if(response.status == 200) {
          this.$store.dispatch("getcategory", response.data);
        }
      })
    },
    getchanelData() {
      getchannel().then(response => {
        if (response && response.data) {
          let alliteam = {
            id: "0",
            code: "all",
            name: "分类",
            img_url: ""
          };
          response.data.unshift(alliteam);
          response.data.forEach(iteam => {
            switch (iteam.code) {
              case "stb": {
                iteam.img_url =
                  "data:image/png;base64,";
                break;
              }
              case "jd": {
                iteam.img_url =
                  "data:image/png;base64,";
                break;
              }
              case "leading": {
                iteam.img_url =
                  "http://ncc-yc-cpumall-ec.img-cn-beijing.aliyuncs.com/b18j8s6c/d48cbe77-61d8-4dd7-97e2-9a90a7448028_领先未来.png";
                break;
              }
              case "suning": {
                iteam.img_url =
                  "http://ncc-yc-cpumall-ec.img-cn-beijing.aliyuncs.com/b18j8s6c/46a93aec-ec46-48e0-baa9-e370a496f552_苏宁.png";
                break;
              }
              default: {
                iteam.img_url =
                  "data:image/png;base64,";
              }
            }
          });
          this.$store.dispatch("querychannel", response.data);
        }
      });
    }
  },
  data() {
    return {
      swipeImg: swipeImg
    }
  }
}
</script>

<style lang="less" scoped>
.home {
  position: relative;
}
.swipe-box {
  height: 9rem;
  img {
    width: 100%;
    height: 100%;
  }
  .imginfo {
    position: absolute;
    left: 10px;
    top: 6px;
    font-size: 0.75rem;
    color: #dddddd;
  }
}
</style>
